import React, { useState } from 'react';
import Button from './components/Button';
import Counter from './components/Counter';

import './App.css';

function App() {
  // const abc = useState(1);
  // typeof abc; // array;
  // abc[0]; // state value itself
  // abc[1]; // state updater for this state

  const [counters, setCounters] = useState([{ count: 0 }]);

  const addCounter = () => {
    //add obj to counters array
    setCounters(counters => [...counters, { count: 0 }]);
  };

  return (
    <div>
      {/* this button will be used to add more counters */}
      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
        <Button buttonLabel="Add Counter!" onClick={addCounter} />
        <Button buttonLabel="+1" onClick={addCounter} />
        <Button buttonLabel="+2" onClick={addCounter} />
        <Button buttonLabel="+4" onClick={addCounter} />
      </div>
      {counters.map((item, index) => (
        <Counter key={index} />
      ))}
    </div>
  );
}

export default App;
